export class Suma {
  constructor() {
    this.edadUno = 0;
    this.edadDos = 0;
    this.resultado = 0;
  }

  suma() {
    this.resultado = Number(this.edadUno) + Number(this.edadDos);
    let sumaTxt = document.getElementById('inputSuma');
    let resultadoTxt = document.getElementById('inputPromedio');
    sumaTxt.value = `${this.resultado}`;
    resultadoTxt.value = `${this.resultado / 2}`;
  }

  limpiar() {
    let sumaTxt = document.getElementById('inputSuma');
    let resultadoTxt = document.getElementById('inputPromedio');
    sumaTxt.value = "";
    resultadoTxt.value = "";
    let edadUno = document.getElementById('edadUno');
    let edadDos = document.getElementById('edadDos');
    edadUno.value = "";
    edadDos.value = "";
  }

}
